export const questions_2 = [
  {
    questionText: "Что такое react?",
    answerOptions: [
      {
        text: "это код, который используется для структурирования и отображения веб-страницы и её контента.",
        isCorrect: false,
      },
      {
        text: "менеджер пакетов, входящий в состав Node. js.",
        isCorrect: false,
      },
      {
        text: "React — это фреймворк для языка программирования JavaScript?,",
        isCorrect: true,
      },
      {
        text: "это язык программирования, который добавляет интерактивность на ваш веб-сайт",
        isCorrect: false,
      },
    ],
  },
  {
    questionText: "Что такое hook ...",
    answerOptions: [
      {
        text: " расширение синтаксиса JavaScript. Этот синтаксис выглядит как язык шаблонов, но наделён всеми языковыми возможностями JavaScript ",
        isCorrect: false,
      },
      {
        text: "Хуки — это технология, которая перехватывает вызовы функций и помогает использовать возможности Reac",
        isCorrect: true,
      },
      { text: "это язык программирования, который добавляет интерактивность на ваш веб-сайт", isCorrect: false },
      { text: "", isCorrect: false },
    ],
  },
  {
    questionText: "что такое useReducer",
    answerOptions: [
      {
        text: "useReducer() – это метод из React Hooks API, похожий на useState, но дающий больший контроль над управлением состояния?",
        isCorrect: true,
      },
      { text: "кокое то ответ", isCorrect: false },
      { text: "кокое то ответ", isCorrect: false },
      { text: "кокое то ответ", isCorrect: false },
